//1Ô∏è‚É£ –§—É–Ω–∫—Ü–∏—è –∑–∞ –ø–æ–∑–¥—Ä–∞–≤
// –ó–∞–¥–∞—á–∞: –ù–∞–ø—Ä–∞–≤–∏ —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—è—Ç–æ –ø—Ä–∏–µ–º–∞ –∏–º–µ –∏ –≤—Ä—ä—â–∞ –ø–æ–∑–¥—Ä–∞–≤.
//üîπ –ü—Ä–∏–º–µ—Ä:
function greet(name) {
  return "–ó–¥—Ä–∞–≤–µ–π," + name + "!";
}
console.log(greet("stoy"));

// 2Ô∏è‚É£ –§—É–Ω–∫—Ü–∏—è –∑–∞ —É–º–Ω–æ–∂–µ–Ω–∏–µ
// –ó–∞–¥–∞—á–∞: –ù–∞–ø—Ä–∞–≤–∏ —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—è—Ç–æ –ø—Ä–∏–µ–º–∞ –¥–≤–µ —á–∏—Å–ª–∞ –∏ –≥–∏ —É–º–Ω–æ–∂–∞–≤–∞.
function area(a, b) {
  return a * b;
}
console.log(area(5, 5));

// 3Ô∏è‚É£ –§—É–Ω–∫—Ü–∏—è –∑–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞–ª–∏ —á–∏—Å–ª–æ –µ –ø–æ–ª–æ–∂–∏—Ç–µ–ª–Ω–æ
// –ó–∞–¥–∞—á–∞: –ù–∞–ø—Ä–∞–≤–∏ —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—è—Ç–æ –∫–∞–∑–≤–∞ –¥–∞–ª–∏ –¥–∞–¥–µ–Ω–æ —á–∏—Å–ª–æ –µ –ø–æ–ª–æ–∂–∏—Ç–µ–ª–Ω–æ –∏–ª–∏ –æ—Ç—Ä–∏—Ü–∞—Ç–µ–ª–Ω–æ.
function checkNumber(num) {
  return num >= 0 ? "–ü–æ–ª–æ–∂–∏—Ç–µ–ª–Ω–æ" : "–û—Ç—Ä–∏—Ü–∞—Ç–µ–ª–Ω–æ";
}

console.log(checkNumber(5));
console.log(checkNumber(-3));
console.log(checkNumber(0));

// 4Ô∏è‚É£ –§—É–Ω–∫—Ü–∏—è –∑–∞ —Å–º—è—Ç–∞–Ω–µ –Ω–∞ –ø–ª–æ—â –Ω–∞ –ø—Ä–∞–≤–æ—ä–≥—ä–ª–Ω–∏–∫
// –ó–∞–¥–∞—á–∞: –ù–∞–ø—Ä–∞–≤–∏ —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—è—Ç–æ –ø—Ä–∏–µ–º–∞ —à–∏—Ä–∏–Ω–∞ –∏ –≤–∏—Å–æ—á–∏–Ω–∞ –∏ –≤—Ä—ä—â–∞ –ø–ª–æ—â—Ç–∞.
function area(width, height) {
  return width * height;
}

console.log(area(5, 10));
console.log(area(3, 7));

//5Ô∏è‚É£ –§—É–Ω–∫—Ü–∏—è –∑–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞–ª–∏ –º–æ–∂–µ—à –¥–∞ –∫–∞—Ä–∞—à –∫–æ–ª–∞
// –ó–∞–¥–∞—á–∞: –ù–∞–ø—Ä–∞–≤–∏ —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—è—Ç–æ –ø—Ä–∏–µ–º–∞ –≤—ä–∑—Ä–∞—Å—Ç –∏ –∫–∞–∑–≤–∞ –¥–∞–ª–∏ –º–æ–∂–µ—à –¥–∞ –∫–∞—Ä–∞—à –∫–æ–ª–∞ (–∞–∫–æ —Å–∏ 18 –∏–ª–∏ –ø–æ–≤–µ—á–µ).
function canDrive(age) {
  return age >= 18 ? "–ú–æ–∂–µ—à –¥–∞ –∫–∞—Ä–∞—à üöó" : "–¢—Ä—è–±–≤–∞ –¥–∞ –ø–æ—á–∞–∫–∞—à ‚åõ";
}

console.log(canDrive(20));
console.log(canDrive(15));

// –ù–∞–ø—Ä–∞–≤–µ—Ç–µ —Ç–∞–∑–∏ —Å—Ç—ä–ø–∫–∞ –æ—Ç –∑–æ–Ω–∞—Ç–∞ —Å–∏ –Ω–∞ –∫–æ–º—Ñ–æ—Ä—Ç, —Å–ø—Ä–µ—Ç–µ —É—Ä–æ–∫–∞, –Ω–∞–º–µ—Ä–µ—Ç–µ –ø—Ä–æ–µ–∫—Ç —Å –æ—Ç–≤–æ—Ä–µ–Ω –∫–æ–¥, –∑–∞ –∫–æ–π—Ç–æ –¥–∞ –¥–æ–ø—Ä–∏–Ω–µ—Å–µ—Ç–µ, –∏ —Å—Ç–∞–Ω–µ—Ç–µ —Ç–æ–≤–∞, –∫—ä–º –∫–æ–µ—Ç–æ —Å—Ç–µ —Ä–∞–±–æ—Ç–∏–ª–∏ ‚Äî —Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫.

// –ù–∞–º–∏—Ä–∞–Ω–µ –Ω–∞ –ø—Ä–∞–≤–∏–ª–Ω–∏—è –ø—Ä–æ–µ–∫—Ç –ø—Ä–∏–º–µ—Ä:  –ø—Ä–æ–≥—Ä–∞–º–∏—Ä–∞–Ω–µ –∏ –ø–æ–º–∞–≥–∞–Ω–µ
